  const [list, setList] = useState<Item[]>([
    { id: 1, name: 'Comprar pão', done: false },
    { id: 1, name: 'Fazer café ', done: false },
  ]);

  return (
    <Components.Container>
      <Components.Area>
        <Components.Header>Lista de Tarefas</Components.Header>

        {/* Área para adicionar tarefa */}

        {list.map((item, index) => (
          <ListItem/>
          // <div>{ item.name }</div>
        ))}

      </Components.Area>
    </Components.Container>
  );
}

export default App;
